import style from "./AvailableMeals.module.css";
import Card from "../UI/Card";
import MealItem from "./MealItem/MealItem";
import { useEffect } from 'react';

const AvailableMeals = () => {
  const DUMMY_MEALS = [
    {
      id: "m1",
      name: "Pangali Virundhu",
      description: "Finest fish and veggies",
      price: 100,
    },
    {
      id: "m2",
      name: "A2B",
      description: "An Indian specialty!",
      price: 20,
    },
    {
      id: "m3",
      name: "Barbecue Burger",
      description: "Indian , Cheesy",
      price: 70,
    },
    {
      id: "m4",
      name: "Green Bowl",
      description: "Healthy...and green...",
      price: 100,
    },
  ];

  const meals = [];

  const loadDataHandler = async () => {
  const response = await fetch('https://food-app-adac7-default-rtdb.firebaseio.com/meals.json')
  const data = await response.json();
  for(const key in data){
    const meal = {
      id : key,
      name : data[key].name,
      price : data[key].price,
      description : data[key].description
    }
    meals.push(meal)
  }
  }


  useEffect(() => {
    loadDataHandler();

  }, [])

  return (
    <section className={style.meals}>
      <Card>
        <ul>
          {DUMMY_MEALS.map((meal) => {
            return <MealItem key={meal.id} meal={meal} id={meal.id}/>;
          })}
        </ul>
      </Card>
    </section>
  );
};

export default AvailableMeals;
